INSERT INTO `wz_affiche` (`id`, `title`, `css`, `publisher`, `content`, `note`, `addtime`, `endtime`, `sort`, `status`) VALUES
  (2, '五指CMS开始预售！', '', 'wuzhicms', '<p>五指CMS开始预售！<br/>详情请登陆 <a href="http://www.wuzhicms.com" _src="http://www.wuzhicms.com">http://www.wuzhicms.com</a>&nbsp;</p>', '', 1415763216, 1415721600, 0, 2),
  (3, '首页推荐1', 'color:#438ccb;font-weight:bold', 'wuzhicms', '<p>公告来袭！！！</p>', '公告来袭！！！', 1416236648, 1416153600, 1, 2),
  (4, '这里是网站公告', '', 'wuzhicms', '<p>&nbsp; &nbsp; &nbsp; &nbsp;公告是行政公文的主要文种之一，它和通告都属于发布范围广泛的晓谕性文种。公告是向国内外宣布重要事项或者法定事项时使用的公文。适用于向国内外宣布重要事项或者法定事项。</p><p>公告是用于向国内外宣布重要事项或者法定事项的公文。公告主要有两种，一是宣布重要事项，如最近中国将在东海进行地对地导弹发射训练；二是宣布法定事项，如宣布某项法规或规章，宣布国家领导人选举结果。</p><p>有一类公告是属于专业性的或向特定对象发布的，如经济上的招标公告，按国家专利法规定公布申请专利的公告，属专业性公告；也有按国家民事诉讼法规定，法院递交诉讼文书无法送本人或代收人时，可以发布公告间接送达，是向特定对象发布的，这些都不属行政机关公文。</p><p>然而，公告在实际使用中，往往偏离了《国家行政机关公文处理办法》中的规定，各机关、单位、团体事无巨细经常使用公告。公告的庄重性特点被忽视，只注意到广泛性和周知性，以致使公告逐渐演变为“公而告之”。</p><p><br/></p>', '', 1414642674, 1414598400, 0, 1),
  (6, '首页推荐1', 'color:#855fa8;font-weight:bold', 'wuzhicms', '<p>xx</p>', '', 1416237800, 1416153600, 0, 1),
  (7, '首页推荐1', 'color:#855fa8;font-weight:bold', 'wuzhicms', '<p>xx</p>', '', 1416237794, 1416153600, 0, 1);
